import React from 'react'
import moment from 'moment'
import styled from 'styled-components'
import { List, ListItemButton } from '@mui/material';

import { chats } from '../../db'

const Container = styled.div`
  height: calc(100% - 56px);
  overflow-y: overlay;
`

const StyledList = styled(List)`
  padding: 0 !important;
`

const StyledListItem = styled(ListItemButton)`
  height: 76px;
  padding: 0 15px;
  display: flex;
`;

const ChatPicture = styled.img`
  height: 50px;
  width: 50px;
  object-fit: cover;
  border-radius: 50%;
`;

const ChatInfo = styled.div`
  width: calc(100% - 60px);
  height: 46px;
  padding: 15px 0;
  margin-left: 10px;
  border-bottom: 0.5px solid silver;
  position: relative;
`;

const ChatName = styled.div`
  margin-top: 5px;
`;

const MessageContent = styled.div`
  color: gray;
  font-size: 15px;
  margin-top: 5px;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
`;

const MessageDate = styled.div`
  position: absolute;
  color: gray;
  top: 20px;
  right: 0;
  font-size: 13px;
`;

const ChatsList: React.FC = () => (
  <Container>
    <StyledList>
      {chats.map(({ id, picture, name, lastMessage }) => {
        console.log(lastMessage)
        return (
          <StyledListItem key={id} >
            <ChatPicture src={picture} alt='Profile pic' />
            <ChatInfo>
              <ChatName>{name}</ChatName>
              <MessageContent>{lastMessage?.content}</MessageContent>
              <MessageDate>{moment(lastMessage?.createdAt).format('HH:mm')}</MessageDate>
            </ChatInfo>
          </StyledListItem>
        )
      })}
    </StyledList>
  </Container>
)

export default ChatsList
